import { reciprocalCycle } from 'lib/reciprocalCycle'

describe('repeatingDecimals', () => {
  test.each`
    n      | exp
    ${2}   | ${''}
    ${3}   | ${'3'}
    ${4}   | ${''}
    ${6}   | ${'6'}
    ${7}   | ${'142857'}
    ${13}  | ${'076923'}
    ${28}  | ${'571428'}
    ${51}  | ${'0196078431372549'}
    ${92}  | ${'0869565217391304347826'}
    ${96}  | ${'6'}
    ${97}  | ${'010309278350515463917525773195876288659793814432989690721649484536082474226804123711340206185567'}
    ${983} | ${''}
  `('$n', ({ n, exp }) => {
    expect(reciprocalCycle(n)).toEqual(exp)
  })

  test('n lower than 2 throws error', () => {
    expect(() => reciprocalCycle(1)).toThrowError(/must be 2 or larger/)
  })
})
